node.doctext:
[%
my $room = $$USER{in_room};
$room ||= 0;
my $RNODE = getNodeById($room);
my $str;
use XML::Generator;
my $XG = new XML::Generator;
#my $linkExpand = !($query->param('staysquare'));


#hack-fix for clients that expect date and time to be mashed together
#things got changed when mySQL was upgraded
#/msg N-Wing if anything is missing from here
#N-Wing, 2005-03-11(5)
my $oldUA = $query->user_agent();
if(
	(index($oldUA,'JChatter/0.1.12.1beta')!=-1)
) {	#TODO other user-agent tests here
	$oldUA = 1;
} else {
	$oldUA = 0;
}


# Avoid a table scan -- [call]
my $csr = $DB->sqlSelectMany('*', 'message', "for_user=0 and room=$room and tstamp > date_sub(now(), interval 500 second)", 'order by tstamp');
# my $csr = $DB->sqlSelectMany('*', 'message', "for_user=0 and room=$room and now()-tstamp < 500", 'order by tstamp');

#my $lines;
my @msgs;
while (my $MSG = $csr->fetchrow_hashref) {
#	$lines++;
	push @msgs, $MSG;
}
#Thu Apr 4, 2002 (N-Wing) zapped this - not really any reason to limit things
#if($lines >= 15) {
#	@msgs = splice(@msgs, -15);
#} 

my $V = getVars(getNode('room topics','setting'));
my $topic = '';
if(exists $$V{$room}) {
	$topic = $$V{$room};
	$topic = $query->escape($topic);
}

$str.=$XG->INFO({site => $Everything::CONF->{site_url}, sitename => $Everything::CONF->{system}->{site_name}, servertime=>scalar(localtime(time)), room => $$RNODE{title}, topic => $topic}, "Rendered by the Chatterbox XML Ticker, which has been deprecated since 2002. Use the universal message xml ticker instead.");

my $m;
my $mTime;
foreach my $MSG (@msgs) {
	my $FUSER = getNodeById($$MSG{author_user});
	$m = $$MSG{msgtext};
#	$m = encodeHTML($m);
#	$m =~ s/\&/\&amp\;/g;
#	$m =~ s/\</\&lt\;/g;
#	$m =~ s/\>/\&gt\;/g;
#	$m =~ s/\"/\&quot\;/g;
#this is done by makeXmlSafe

	#ascorbic's dangling [ fixer, but may be better in makeXmlSafe
	$m =~ s/\[([^\]]*?)$/&#91;$1/;

	use Everything::XML; 
	$m = Everything::XML::makeXmlSafe($m);

	$mTime = $$MSG{tstamp};
	if($oldUA) {
		#convert from 'yyyy-mm-dd hh:mm:ss' to 'yyyymmddhhmmss'
		if($mTime =~ /^(\d{4})-(\d\d)-(\d\d) (\d\d):(\d\d):(\d\d)$/) {
			$mTime = $1.$2.$3.$4.$5.$6;
		} else {
$mTime .= ' no';
}
	}

	$str.= "\n".$XG->message({
		author => Everything::XML::makeXmlSafe($$FUSER{title}),
		time => $mTime,
	}, "\n".$m);
       	#TODO add the room id (for superhearing thingy)
}

#$str .= "\n".$XG->message({author=>'N-Wing','time'=>'2005-03-12 01:10:01'}, "\n" . Everything::XML::makeXmlSafe($query->user_agent()) );

"\n".$XG->CHATTER($str."\n");

%]
