node.doctext:
[%

my $cache = $DB->stashData("coolnodes");

my $xml;
use XML::Generator;
my $XG = new XML::Generator;

my $count = 15;
my %used;

foreach my $CW (@$cache) {
  next if exists $used{$$CW{coolwriteups_id}};
  $used{$$CW{coolwriteups_id}} = 1;
  $xml .="\t".$XG->cooled({node_id => $$CW{coolwriteups_id}, 
     parent_e2node => $$CW{parentNode}, 
     author_user => $$CW{wu_author},
     cooledby_user => $$CW{cooluser}},
     $$CW{parentTitle})."\n";
  last unless (--$count);
}

$xml = $XG->COOLEDNODES(
   $XG->INFO({site => $Everything::CONF->site_url, sitename => $Everything::CONF->system->{site_name},  servertime => scalar(localtime(time))}, 'Rendered by the Cool Nodes XML Ticker')
   . "\n".$xml . "\n");

$xml;
%]
