my $str = '';

my $unAchieved = $DB->sqlSelectMany(
	'display'
	, "achievement LEFT OUTER JOIN achieved
		ON achieved_achievement=achievement_id
		AND achieved_user=$$USER{node_id}"
	, 'achievement_still_available=1
	   AND achieved_achievement IS NULL'
);

while (my $u = $unAchieved->fetchrow_hashref) {
   $str.= "<li>$$u{display}</li>";
}

return $query -> ul($str);
