node.code:
my ($limit) = @_;

$limit ||= 50;

#my $batch = $DB->sqlSelect("max(batch)", "newnodes");

my $qry = "SELECT * FROM newwriteup, node where newwriteup.node_id=node.node_id 
         ";

my $uid = getId($USER);
my $isEd = (exists $HTMLVARS{'group_content editors'}{$uid}) || (exists $HTMLVARS{'group_gods'}{$uid});

my $nr = getId(getNode('node row', 'superdoc'));
my $nid;
my $kuid;

$qry.= 'and notnew=0 ' unless $isEd;
$qry.= " order by newwriteup_id DESC LIMIT $limit";


my $csr = $Everything::dbh->prepare($qry);

$csr->execute or return 'Ack! Error in executing query: '.$qry;
my $count=0;
my @colors = ('#ccccff', '#ffffcc'); #FIXME theme-driven

my $str ='';
while(my $N = $csr->fetchrow_hashref){
  $nid = $$N{node_id};
  $N = getNode $nid;
  $str .= '<tr bgcolor="'.$colors[++$count%2].'">';


  $str.='<td align="left">'
    .linkNode($N, 0, {displaytype => "printable"})
    .'</td><td align="right">'
    .($$N{author_user})
    .'</td>' if ($$N{title});

  $str.="</tr>\n";
}
$csr->finish;

$str;
