node.code:
my $uid = shift;
$uid ||= $USER->{node_id};
my $lookingAtSelf = (!$uid || $uid == $$USER{node_id});
my $doConversion = ($query -> param('converttodrafts') == 1 && $lookingAtSelf);
my $listOnly = !$doConversion;
my ($str, $convertButton) = ('', '');

unless ($doConversion || !$lookingAtSelf) {
	$convertButton .=
		htmlcode('openform')
		.$query -> hidden('converttodrafts', 1)
		.htmlcode('closeform', 'Convert existing scratch pads to drafts')
		;
}

my $select = "scratch_private as publication_status,
	scratch_collab as collaborators,
	scratch_text as doctext,
	scratch_id,
	scratch_user as author_user";

my $uname = ($listOnly ? getNodeById($uid) : $USER) -> {title};

$DB->sqlDelete('scratch',
	"scratch_id = $$USER{node_id}
		AND scratch_text = ''");

my $cuss = $dbh -> prepare(
	"SELECT
		$select,
		'$uname''s default scratch pad' as title
	FROM scratch
	WHERE scratch_id = ?
	UNION
	SELECT
		$select,
		scratch_title as title
	FROM scratch2
	WHERE scratch_user = ?
	ORDER BY title"
);

$cuss -> execute($uid || $$USER{node_id}, $uid || $$USER{node_id});

my $status = {
	private => getNode('private', 'publication_status') -> {node_id},
	public => getNode('public', 'publication_status') -> {node_id},
	shared => getNode('shared', 'publication_status') -> {node_id},
};

my $d;
my $allOK = 1;
my $SP = getNode('Scratch Pads', 'superdocnolinks');

while (my $P = $cuss -> fetchrow_hashref()){
	my $statusTitle = 'public';
	if ($$P{publication_status}){ # was private
		$statusTitle = ($$P{collaborators} ?
			'shared' : 'private');
	}

	$$P{publication_status} = $$status{$statusTitle};
	$$P{title} = cleanNodeName($$P{title});

	if ($listOnly){
		$str .= '<li>'.linkNode($SP, $$P{title}, {scratch_id => $$P{scratch_id}, other_user => $uname}).'</li>'
			if htmlcode('canseedraft', $P) == 1;
	}elsif ($d = $DB -> insertNode($$P{title}, 'draft', $USER, $P)){
		$str .= '<li>'.linkNode($d)
			."<em> ($statusTitle)</em> - OK</li>";
		unless ($$P{scratch_id} == $$USER{node_id}){
			$DB->sqlDelete('scratch2',
				"scratch_id = $$P{scratch_id} AND
				scratch_user = $$USER{user_id}");
		}else{
			$DB->sqlDelete('scratch', "scratch_id = $$USER{node_id}");
		}
	}else{
		$str .= "<li><b>$$P{title}: import failed</b></li>";
		$allOK = 0;
	}
}

unless ($str){
	return '' if $uid;
	return '<p>Nothing to convert.</p>';
}

$str = "<ol>$str</ol>";
$str .= '<p>Problem in conversion. Please <strong>'
	.linkNode($SP, 'try again', {converttodrafts => 1})
	.'</strong>, and if things still
	don\'t work properly, complain to [DonJaime[user]].' unless $allOK;

return "<h2>Converted scratch pads to drafts:</h2>
$str" unless $listOnly;

'<h2>'.linkNode($uid)."'s scratch pads:</h2>
$convertButton
$str";
