node.code:
my $ntypet = $$NODE{type}{title};
return unless ($ntypet eq 'e2node' || $ntypet eq 'superdoc' || $ntypet eq 'superdocnolinks' || $ntypet eq 'document');

my $COOLLINK = getNode('coollink','linktype') -> {node_id};

my $link = $DB->sqlSelectHashref('to_node', 'links', 'from_node='.$$NODE{node_id}.' and linktype='.$COOLLINK.' limit 1') ;

return '<span id="editorcool"><strong>cooled by</strong> '.linkNode($$link{to_node}).'<br>
</span>' if $link ;

return '';
