node.code:
my ($user_id) = shift;

$user_id = int(getId $user_id);
$user_id ||= 0;

my ($scratch_count) =
  $DB->sqlSelect(
    "SUM(scratch_count)"
    , "(
      SELECT COUNT(scratch_id) 'scratch_count'
        FROM scratch
        WHERE scratch_id = $user_id

      UNION ALL

      SELECT COUNT(scratch_id) 'scratch_count'
        FROM scratch2
        WHERE scratch_user = $user_id
    ) `scratch_counts`
    "
  );

return $scratch_count;
