node.code:
# This is code mostly by [rootbeer277], originally in the [messsage]
# opcode. [Swap] just moved it to its own htmlcode and hacked on top 
# of it a little.
use Everything::Room;

my ($input) = @_;

#Whitespace separated list of users to drag.
my @users = split " ",$input;

#Some "globals"
my $UID = $$USER{node_id};
my $lockminutes = 30;
my $lockseconds = $lockminutes * 60;
my $dragroomname = "Debriefing Room";
my $dragroom = getNode( $dragroomname, 'room' );
my $dragmsg;

# tell dragger if room does not exist
if (!$dragroom) {
  $dragmsg = "$dragroomname does not exist, that's odd.";
  $HTMLVARS {msgparams} = {
                           'author_id' => getId(getNode('Klaproth', 'user')),
                           'recipient_id' => $UID,
                           'message' => $dragmsg,
                           };
  htmlcode('sendPrivateMessage','');
  return;
}

local *dragThem = sub{
  # set up variables
  my $dragmsg;
  my $dragusername = $_[0];
  my $draguserid;      # fill in after we've confirmed the user exists
  my $draguser = getNode( $dragusername, 'user' );

  # check if the username's spaces are replaced with _ if not found
  if (!$draguser) {
    $dragusername =~ s/\_/ /gs;
    $draguser = getNode( $dragusername, 'user' );
  }
  # if still not found, tell dragger user does not exist
  if (!$draguser) {
    $dragmsg = "$dragusername does not exist.";
    $HTMLVARS {msgparams} = {
                             'author_id' => getId(getNode('Klaproth', 'user')),
                             'recipient_id' => $UID,
                             'message' => $dragmsg,
                             };
    htmlcode('sendPrivateMessage','');
    return;
  }


  # All potential problems averted, we now actually drag the user
  # kicking and screaming into $dragroomname
  else {
    $draguserid = $$draguser{'node_id'};
    changeRoom( $draguser, $dragroom );
    changeRoom( $USER, $dragroom );
#    $dragmsg = "You have been dragged into $dragroomname."; ## old message
    $dragmsg = "You are being taken to $dragroomname by ".linkNode($UID).", who will join you there.";
    $HTMLVARS {msgparams} =
      {
       'author_id' => getId(getNode('Klaproth', 'user')),
       'recipient_id' => $draguserid,
       'message' => $dragmsg,
       };
    htmlcode('sendPrivateMessage','');

    htmlcode('addNotification', 'chanop dragged user', 0, {
      chanop_id => $USER->{node_id},
      user_id => $draguserid,
      room_id => $dragroom->{node_id}
    });

    # To lock in:
    my $V = getVars($draguser);
    ++$$V{numborged};
    $$V{lockedin} = time + $lockseconds;
    setVars($draguser,$V);

    return;
  }
};

foreach my $user(@users){
  dragThem($user);
}
