node.code:
return unless $APP->isEditor($USER);

my $defaultreason="";

if($query->param('nodeunlock'))
{
   $defaultreason = $DB->sqlSelect("nodelock_reason", "nodelock", "nodelock_node=$$NODE{node_id}");
   $DB->sqlDelete("nodelock","nodelock_node=$$NODE{node_id}");
}

if($query->param('nodelock'))
{
   $DB->sqlInsert("nodelock", {nodelock_reason => $query->param('nodelock_reason'), nodelock_user => $$USER{user_id}, nodelock_node => $$NODE{node_id}}) unless ($DB->sqlSelectHashref("*", "nodelock", "nodelock_node=$$NODE{node_id}"));

}

my $nodelock = $DB->sqlSelectHashref("*", "nodelock", "nodelock_node=$$NODE{node_id}");

my $str ='<p align="right">';



if($nodelock)
{
  my $locker = getNodeById($$nodelock{nodelock_user});

  $str .= htmlcode('openform').'(Locked by '.linkNode($locker, $$locker{title}).')<input type="hidden" name="nodeunlock" value="'.$$NODE{node_id}.'"><input type="submit" value="Unlock this e2node"></form>';

}
else
{
  $str .= htmlcode('openform').'Softlock this node because: <input type="hidden" name="nodelock" value="'.$$NODE{node_id}.'"><input type="text" name="nodelock_reason" value="'.$defaultreason.'"><br><input type="submit" value="Softlock this e2node"></form>';

}

$str .='</p>';

return parseLinks($str);
