node.code:
if ( $APP->isEditor($USER) ){
	my $str ="
	<div id='admintools'>
	<h3>Admin Tools</h3>";

	$str .= htmlcode("addfirmlink");
	unless ($$NODE{group} && @{ $$NODE{group} }){
		$str .= htmlcode("addnodeforward")
			.htmlcode('openform')
			.'<fieldset><legend>Delete nodeshell</legend>
			Usually you should only delete a nodeshell if it is egregiously offensive
			or was created by mistake. If there is anything else wrong with it,
			you should just correct the spelling.<br>'
			.$query -> submit('sumbit', 'Delete')
			.$query -> checkbox(
				-name => 'confirmop'
				, value => 'nuke'
				, label => 'I really mean this')
			.'</fieldset></form>';
	}else{
		$str .= htmlcode("ordernode");
	}

	$str .= htmlcode('openform')
		.'<fieldset><legend>Change title</legend>';

	my $newTitle = $query->param('e2node_title');
	if ($newTitle ne $query->param('oldTitle')){
		# repair node if title has changed
		if ($$NODE{title} eq $newTitle) {
		   htmlcode('repair e2node', $NODE, 'no-reorder');
		   $str .= "Repaired node to rename all contained writeups.";

		}else{
		# failed rename
			my $reason = getNode($newTitle, 'e2node');
			$str .= $reason
				? linkNode($reason)
				.' already exists. '
				.linkNode(getNode('Magical Writeup Reparenter', 'superdoc')
					, 'Move all writeups &hellip;'
					, {old_e2node_id => $$NODE{node_id}
						, new_e2node_id => $$reason{node_id}
						, reparent_all => 1
					}
				) 
				: 'Ack! Rename failed.';
		}
		$str .= '<br>';
	}

	$str .= htmlcode('textfield', 'title')
		.$query -> hidden(
			-name => 'oldTitle'
			, value => $$NODE{title}
			, force => 1
		)
		.$query -> submit('rename', "Rename")
		.'</fieldset></form>';

	$str .= htmlcode("softlock");

	return "$str\n</div>";
}
