my $bgcolor ||= $$VARS{ ekw_bgcolor };
$bgcolor ||= $$THEME{ bodyBgcolor };
$bgcolor ||= "#FFFFFF";

my $textcolor = $$VARS{ ekw_textcolor };
$textcolor ||= $$THEME{ bodyTextcolor };
$textcolor ||= "#000000";

my $linkcolor = $$VARS{ ekw_linkcolor };
$linkcolor ||= $$THEME{ bodyLinkcolor };
$linkcolor ||= "#993333";

my $vlinkcolor = $$VARS{ ekw_vlinkcolor };
$vlinkcolor ||= $$THEME{ bodyVlinkcolor };
$vlinkcolor ||= "#333399";

my $alinkcolor = $$VARS{ ekw_alinkcolor };
$alinkcolor ||= $$THEME{ bodyAlinkcolor };
$alinkcolor ||= "#660000";

"<body\ntext=\"$textcolor\" bgcolor=\"$bgcolor\" link=\"$linkcolor\" alink=\"$alinkcolor\" vlink=\"$vlinkcolor\"\nleftmargin=\"2\" marginleft=\"2\" rightmargin=\"2\" marginright=\"2\"\n>";
