node.code:
# displays footer information in its own table
# originally in [jukka container], adapted for ekw theme

my $str = '<table cellpadding="3" cellspacing="0" border="0" width="100%">'."\n".
	'<tr><td><img src="http://static.everything2.com/dot.gif" width="1" height="25" alt=""></td></tr>'."\n<tr";
$str .= '><td align="center">';

# Pseudorandom letter in range 'A'..'Z'
my $letter = chr( int( 65 + rand( 26 ) ) );

my @R = (
	'This affordable entertainment brought to you by',
	'E2 is a by-product of the existence of',
	"Everything 2 is brought to you by the letter $letter and",
	'This page courtesy of',
);

$str.=$R[int(rand(@R))];
$str.=' <a href="/node/usergroup/edev">The Everything Development Company</a>';

if ( rand() < 0.1 ) {
    my @gibberish = (
        "We are the bat people.", "Let sleeping demons lie.",
        "Monkey! Bat! Robot Hat!", "We're sneaking up on you.",
    );
    $str .= '<br /><small><i>' . $gibberish[int(rand(@gibberish))];
    $str .= '</i></small>';
}

my $NL = getNode('tagline grab', 'nodelet');
$str.= '<br />'.$$NL{nltext};

$str .= "</td></tr>";
$str .= "\n</table>\n";

$str;
