node.code:
my ($sql) = join(',', @_);
my $res = $dbh->prepare('explain ' . $sql);
$res->execute() or return 'Oops ' . $sql;

my $str = '<table style="width: 100%"><tr><th>table</th><th>type</th><th>possible_keys</th><th>key</th><th>key_len</th><th>ref</th><th>rows</th><th>Extra</th></tr>';

my $row = 1;
while (my $r = $res->fetchrow_hashref) {
   $str .= sprintf('<tr class="%s"><td>%s</td><td>%s</td><td>%s</td><td>%s</td><td>%s</td><td>%s</td><td>%s</td><td>%s</td></tr>',
                  $row ? 'oddrow' : 'evenrow',
                  $$r{table},
                  $$r{type},
                  $$r{possible_keys},
                  $$r{key},
                  $$r{key_len},
                  $$r{ref},
                  $$r{rows},
                  $$r{Extra}
                  );
   $row = !$row;
}

$str .= '</table>';
$str;
