node.code:
my $forwd = $ENV{HTTP_X_FORWARDED_FOR} || "";
my $remote = $ENV{REMOTE_ADDR} || "";

my @addrs =
  grep { $APP->isIpRoutable($_) } # ignore our Pound server
    grep { /\S/ }
      split /\s*,\s*/,
        ",$forwd,$remote";

return @addrs if wantarray;

my $addr = '' . join ',', @addrs;
return $addr;
