node.code:
my ($ad_location) = @_;
my $node_id = $$NODE{node_id};

my @badnodes = qw(1921577 1369958 2003596 29906 149007 1332646 1330152 59705);

foreach my $nid (@badnodes) {
   return "<!-- noad:badnode -->" if $node_id == $nid or $$NODE{type}{title} eq 'writeup' && $$NODE{parent_e2node} == $nid;
   if (exists $$NODE{linklist}) {
      foreach my $l (@{ $$NODE{linklist} }) {
         return "<!-- noad:badnodelink -->" if $l->{to_node} == $nid;
      }
   }
}


my @badwords = qw(boob breast butt ass lesbian cock dick penis sex oral anal drug pot weed crack cocaine fuck wank whore vagina vag cunt tits titty twat shit slut snatch queef queer poon prick puss orgasm nigg nuts muff motherfuck jizz hell homo handjob fag dildo dick clit cum bitch rape ejaculate bsdm fisting balling masturbation);

foreach my $word (@badwords) {
        return "<!-- noad:badword -->" if $$NODE{title} =~ /\b$word/i or $$NODE{title} =~ /$word\b/i;
        if (exists $$NODE{linklist}) {
          foreach my $l (@{ $$NODE{linklist} }) {
              my $title = getNode($l->{to_node})->{title};
              return "<!-- noad:$title/$word -->" if $title =~ /\b$word/i or $title =~ /$word\b/i;
          }
        }
}

return "<!-- noad:nothingfound -->" if $node_id == getNode('Nothing Found', 'superdoc')->{node_id};
return "<!-- noad:findings -->" if $node_id == getNode('Findings:', 'superdoc')->{node_id};
return "<!-- noad:badnodeid -->" unless ($node_id =~ /^\d+$/);
return "<!-- noad:https -->" if $query->url =~ /^https/;

my ($ad_width, $ad_height, $ad_format, $ad_type) = (undef,undef,undef,undef);

if ($ad_location eq 'nodelet') {

  $ad_width =  160;
  $ad_height = 600;
  $ad_format = "160x600_as";
  $ad_type = "text_image";

# default case, 'topbanner'
} else {

  $ad_width =  728;
  $ad_height = 90;
  $ad_format = "728x90_as";
  $ad_type = "text_image";

}

my $ad = <<ADTEXT;
<center>
<script type="text/javascript"><!--
google_ad_client = "ca-pub-0613380022572506";
/* E2 top banner */
google_ad_slot = "9636638260";
google_ad_width = 728;
google_ad_height = 90;
//-->
</script>
<script type="text/javascript" src="http://pagead2.googlesyndication.com/pagead/show_ads.js"></script>


</center>
ADTEXT

return $ad;
