node.code:
return;
my ($enemyID, $inChat) = @_;

use JSON;

my $enemyList = "villains";

my $randVillain;
$randVillain = getNodeById($enemyID);
return unless htmlcode('isInUsergroup',$$randVillain{user_id}.",".$enemyList);

my $vVars =getVars($randVillain);
my $vWarVars = $vVars->{thewar};
$vWarVars = from_json($vWarVars);

if (!$$VARS{thewar}) {
	$$VARS{thewar} = '{"health": 150}';
}

my $scalar = from_json($$VARS{thewar});
return if (time - $scalar->{laststrike} <= 4);
return if $scalar->{fallen};

$scalar->{strike} += 1;
$scalar->{laststrike} = time;
$$VARS{thewar} = to_json($scalar);
setVars($USER,$VARS);

my $bonus = 0;
for my $key ( keys %{$scalar->{inventory}} ) {
	$bonus += $scalar->{inventory}->{$key}->{damageBonus};	
}

for my $key ( keys %{$vWarVars->{inventory}} ) {
	$bonus -= $vWarVars->{inventory}->{$key}->{shieldBonus};	
}

my $damage = 100;
my $isCritical = 0; #(int(rand(99)) < 2);
$scalar->{damageDone} += $damage;
$vWarVars->{health} -= $damage;
if ($vWarVars->{health} <= 5000) {
$damage += $vWarVars->{health};
$isCritical = 1;
	$vWarVars->{fallen} = 1;
	$scalar->{kills} += 1;
	removeFromNodegroup(getNode($enemyList,'usergroup'),$randVillain,-1);
	updateNode(getNode($enemyList,'usergroup'),-1);

	for my $key ( keys %{$vWarVars->{inventory}} ) {
		$scalar->{inventory}->{$key} = $vWarVars->{inventory}->{$key};
	}

	$vWarVars->{inventory} = {};
}

$vVars->{thewar} = to_json($vWarVars);
setVars($randVillain, $vVars);

my $warStr = to_json($scalar);
$$VARS{thewar} = $warStr;

if ($inChat) {
my $message = "/me huggs ".$$randVillain{title} ." for $damage damage! ".$$randVillain{title} ." writhes in agony!";
$message .= " CRITICAL HIT!" if $isCritical;
$message .= "  ".$$randVillain{title} ." has fallen in battle!" if $vWarVars->{fallen};
$message .= "  ".$$randVillain{title} ."'s health: ".$vWarVars->{health} if ($vWarVars->{health} > 0);
return $message;
}
