node.code:
my $check_wu = shift;
my $wu_id = int(getId($check_wu));
my $noderow = getId(getNode('node row', 'superdoc')) ;

my ($kuid, $unkuid) =
  $DB->sqlSelect(
    'linkedby_user, removedby_user', 'weblog'
    , "weblog_id=$noderow and to_node=$wu_id"
  );
$kuid ||= 0;
$unkuid ||= 0;

return 1 if ($kuid && !$unkuid);
return 0;
