node.code:
my ($PARAM) = @_;

my $default ='';
my $lnid = getId($NODE);
my $ParentNODE = $NODE;
if(getId($USER) != $HTMLVARS{guest_user} and my $ln = $query->param('lastnode_id')) {
 my $LN = getNode $ln;
 if($$LN{type}{title} eq 'writeup') {
  $LN = getNodeById($$LN{parent_e2node});
 } 
 $default = $$LN{title} if $LN;
}

if($$NODE{type}{title} eq 'writeup') {
  $ParentNODE = getNodeById($$NODE{parent_e2node});
} 
 $lnid = $$ParentNODE{node_id} if $ParentNODE;

my $title=$query->param('node');
$query->param('node', $default);  

$str .= $query->start_form("POST",$query->script_name). 
 $query->textfield(-name => 'node',
  -default => $default,
  -size => 28,
  -maxlength => 80);
my $types = getVars(getNode("default searchtypes", "setting"));
#$str.="<input type=\"hidden\" name=\"type\" value=\"$_\" />" foreach(keys %$types);
$str.='<input type="hidden" name="lastnode_id" value="'.$lnid.'" />';

if (exists $$THEME{searchIcon}) {
  $str.= '<input type="image" name="searchy" alt="search" src="'.$HTMLVARS{imageroot}.$$THEME{searchIcon}.'"';
  $str.= ' width="'.$$THEME{searchIconWidth}.'"' if exists $$THEME{searchIconWidth};
  $str.= ' height="'.$$THEME{searchIconHeight}.'"' if exists $$THEME{searchIconHeight};
  $str.=' />';
} else {
 $str.='<input type="submit" name="searchy" value="search" />';
}
 $query->param('node', $title); 

$query->param('soundex', '');
$query->param('match_all', '');
$query->param('nosoftlink', '');

$str.="\n".$query->checkbox(
 -name => 'soundex',
 -value => '1',
 -label => '',
 #-label => 'Near Matches'
);
$str.="<label>Near Matches</label>";

$str.="".$query->checkbox(
 -name => 'match_all',
 -default => '0',
 -value => '1',
 -label => '',
);
$str.="<label>Ignore Exact</label>";

$str;
