node.code:
# Generate a link to a stylesheet, incorporating the version 
# number of the node into the URL. This can be used in conjunction
# with a far-future expiry time to ensure that a stylesheet is
# cacheable, yet the most up to date version will always be
# requested when the node is updated. -- [call]
my ($n, $displaytype) = @_;
$displaytype ||= 'view' ;

unless (ref $n) {
	unless ($n =~ /\D/) {
	  $n = getNodeById($n);
	} else {
	  $n = getNode($n, 'stylesheet');
	}
}
if ($n) {
  my $v = $DB->{cache}->getGlobalVersion($n);

  return urlGen({
  	node_id => $n->{node_id},
  	version => $v,
  	displaytype => $displaytype
  }, 1)
	  if $$USER{node_id} == $$n{author_user};

  my $escapeTitle = CGI::escape($$n{title});
  my $autofixText = "";
  $autofixText = "_autofix"
    if $$USER{ user_id } != $$n{ author_user }
    && $$USER{ user_id } != $HTMLVARS{ guest_user };
  $escapeTitle =~ s/%[0-9A-F]{2}|_//g;
  my $stylesheetFilename = "${escapeTitle}_v$v$autofixText.css"; 
  return "http://jscss.everything2.com/$stylesheetFilename";
} else {
  return $n;
}
