node.code:
my ($index) = @_;

$index or return;

my @group = ();
@group = @{ $$NODE{group} } if $$NODE{group};
@group or return;
$index--;
$index < @group or return;

my $str = "<multicol cols='2'>";


while ($index < @group) {
  my $N = getNodeById $group[$index++];
  my $WR = getNodeById $$N{wrtype_writeuptype};
 $str.="<li>(".linkNode ($N, $$WR{title}).") by ".linkNode($$N{author_user})."</li>\n";
}
$str.="</multicol>";
$str;
