node.code:
my ($uid) = @_;
getRef $uid;
return unless $uid;
return unless($$uid{type_nodetype} == getId(getType('user')));
$$uid{acctlock} = $$USER{user_id};

my $OP = getId(getNode("lockaccount","opcode"));

$DB->sqlInsert('seclog',
  {
    'seclog_user' => $$USER{node_id}
    , 'seclog_node' => $OP
    , 'seclog_details' => "$$uid{title}'s account was locked by $$USER{title}"}
  );

# Delete all public messages from locked user
$DB->sqlDelete('message', "for_user = 0 AND author_user = $$uid{user_id}");

# revert all review drafts to 'findable' status
# (they won't actually be findable unless/until the account is unlocked)
$DB -> sqlUpdate('draft JOIN node ON draft_id=node_id'
	, {publication_status => getId(getNode('findable', 'publication_status'))}
	, "node.author_user = $$uid{node_id} AND
		draft.publication_status = " . getId(getNode('review', 'publication_status')));

# hide the locked user's scratch pad.
my $V = getVars($uid);
delete $$V{skratchShare};
setVars($uid, $V);
updateNode($uid, -1);
