node.code:
return unless isGod($USER);
my $R ||= $$USER{in_room};
return if $R == 0;

getRef($R);

my $open = "1\;";
my $locked = "0\;";

my $title = "";
if($$R{criteria} eq $open) {
	$title = '(lock)';
} elsif($$R{criteria} eq $locked) {
	$title = '(unlock)';
} else {
	return;
}

linkNode($NODE, $title, {op=>'lockroom', target => getId($R)});
