node.code:
return '' if $APP->isGuest($USER);

my $isUsh = sub {
  my $addr = shift;
  return 1 if ($addr eq '24.59.79.225');
  return 0;
};

my @addrs = htmlcode('get ips');
my $addr = join ',', @addrs;

return '' if ($$VARS{ipaddy} eq $addr);
$$VARS{ipaddy} = $addr;

my $hour_limit = 24;
my $ipquery = <<SQLEND;
SELECT DISTINCT iplog_ipaddy
  FROM iplog 
  WHERE iplog_user = $$USER{user_id}
    AND iplog_time > DATE_SUB(NOW(), INTERVAL $hour_limit HOUR)
SQLEND
my $previous_addrs = $DB->getDatabaseHandle()->selectall_arrayref($ipquery);
my %ignore_addrs = ( );

map { $ignore_addrs{$$_[0]} = 1; } @$previous_addrs
  if ($previous_addrs);

map {
  $DB->sqlInsert("iplog", {iplog_user => $$USER{user_id}, iplog_ipaddy => $_})
    if !$ignore_addrs{$_};
} @addrs;

my $ushed = grep { &$isUsh($_) } @addrs;

if ($ushed) {
   $$VARS{infected} = 1;
}

return '';
