node.code:
my $limit = $$VARS{num_newwus} || 15;

#if(!exists($HTMLVARS{'group_gods'}{$$USER{node_id}}) && !exists($HTMLVARS{'group_content editors'}{$$USER{node_id}})){  my $blah = getNodeById(1217114); return $$blah{nltext};}

my $UID = $$USER{user_id} || 0;
my $isRoot = exists($HTMLVARS{'group_gods'}{$UID}) ;
my $cansee = ($isRoot || exists($HTMLVARS{'group_content editors'}{$UID}));



my $str = htmlcode('daylog') ;

$str .= '<ul id="log_list">';

my $nwf = getNodeById(1923732); # hard-coded Test NW Feeder nodelet (Oolong's big feeder is 1923494, Kyle's is 1801407, DREAM feeder is 1923730, LOG feeder is 1923732, I realise this is ugly)
my @wucache = eval('my '.$$nwf{nltext});
my ($found, $count, $iter)  = 0;


if ($cansee) {
  # Admins can suppress display of hidden/deleted writeups - LB 29Apr06
  if ($query->param("gochange")) { # Only swap on a "show" command from NW
    if ($query->param("nw_nojunk")) {
      $$VARS{nw_nojunk} = 1;
    } else {
      $$VARS{nw_nojunk} = 0;
    }
  }
}

while(($found < $limit)&&($iter<10000)) { # Just in case I've messed it up, so it doesn't infinitely loop if there's nothing there
#	$iter++; >>> Causes the miss-most-recent display bug.
	my $N = $wucache[0][$iter];
	last if $N==undef;	#possible since hidden things are skipped
	$iter++;
	$found++;

		$str .= "\n<li class='contentinfo writeupmeta".($$N{notnew} ? ' wu_hide' : '')."'>\n\t\t<span class='author'>".linkNode($$N{author_user}, '', {lastnode_id=>undef}).'</span>';
		if($$N{notnew}){
			$str .= "\n\t\t<span class='hidden'><abbr title='hidden'>(H!)</abbr></span>";
		}
		$str .= "\n\t\t<span class='hidden'>(...)</span>" if $DB->sqlSelect('notnew', 'newwriteup', "node_id=$$N{writeup_id}") != $$N{notnew};        
	        $str .=' (X)' if $DB->sqlSelect('count(*)', 'weblog', "weblog_id=855769 and to_node=$$N{writeup_id} limit 1"); # 855769 - node row hardcoded




		$str .= "\n\t\t<div class='title'>".linkNode($$N{writeup_id}, $$N{parent_title}, {lastnode_id=>undef})."</div>";
		$str.= "\n</li>";

}
if ($found < $limit) {
	$str.='damnit, only found '.$found;
}

$str.='</ul>';

return $str;
