node.code:
my ($limit) = @_;

$limit ||= 50;

my $qry = 'SELECT writeup_id FROM writeup';

my $isEd = $APP->isEditor($USER);

$qry.= ' WHERE notnew=0 ' unless $isEd;
$qry.= " ORDER BY publishtime DESC LIMIT $limit";

my $ed = 'ed,' if $isEd;
my $funk = sub{
		my $N = shift; # $N is a full node by now
		my $str.='<td>';

		if($$N{notnew}){
		  $str .= '(<font color="red">H!</font>)';
                $str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}
					, 'op'=>'unhidewriteup', 'hidewriteup'=>$$N{node_id}}).'>un-h!</a>)';
                }else{
		$str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}
			, 'op'=>'hidewriteup', 'hidewriteup'=>$$N{node_id}}).'>h?</a>)';
                }
		$str .= '(<font color="red">...</font>)'
			if $DB->sqlSelect('notnew', 'newwriteup', "node_id=$$N{node_id}") != $$N{notnew};

		$str.='</td><td>';
		$str.='&nbsp;</td>';
		return $str;
};

my $nids = $dbh -> selectcol_arrayref($qry);

htmlcode('show content', $nids ,
	qq'<tr class="&oddrow">$ed "<td>", parenttitle, type, "</td><td>", listdate, "</td><td>", author, "</td>"', 'ed' => $funk);
