node.code:
my ($limit) = @_;
$limit ||= 15;
if( $APP->isGuest($USER) ){
	my $blah = getNode('New Writeups Normal', 'nodelet');
	return $$blah{nltext};
}

my $UID = $$USER{user_id} || 0;
$limit = $$VARS{num_newwus} if($$VARS{num_newwus});



my $isRoot = exists($HTMLVARS{'group_gods'}{$UID}) ;

my $cansee = ($isRoot || exists($HTMLVARS{'group_content editors'}{$UID}));

my $str = '<table width="100%" border="0" cellpadding="0" cellspacing="0">';
if ($$USER{title} eq 'kthejoker5') {$str .= "!".$limit."!";}

my $count=0;
my @colors;
@colors = split(/\s*,\s*/,$$THEME{themeColors}) if exists $$THEME{themeColors};
@colors = ('#CCCC99') unless @colors;

my $nwf = getNode('Test NW Feeder', 'nodelet'); # this old code uses the new feeder; the 'old' one is newer
my @wucache = eval('my '.$$nwf{nltext});
my $found  = 0;
my $iter = 0;

if ($cansee) {
  # Admins can suppress display of hidden/deleted writeups - LB 29Apr06
  if ($query->param("gochange")) { # Only swap on a "show" command from NW
    if ($query->param("nw_nojunk")) {
#      $VARS->{nw_nojunk} = 1;
       $$VARS{nw_nojunk} = 1;
    } else {
#      $VARS->{nw_nojunk} = 0;
      $$VARS{nw_nojunk} = 0;
    }
  }
}

while($found < $limit) {
	my $N = $wucache[0][$iter];
	last if $N==undef;	#possible since hidden things are skipped
	$iter++;

	if($$N{notnew}) {
		next unless ($cansee); #removed author from cansee code - ktj - 06.06.08
                # Suppress hidden/deleted w/us, if so selected - LB 29Apr06
                next if $$VARS{nw_nojunk};
	} elsif (!$cansee) {
		# Hide unparented writeups from normal users
		if (!getNodeById($$N{parent_e2node}) || $$N{parent_title} eq "") {
			next;
		}
	} else {
		++$found;
	}


	my $clr = $colors[$count++%int(@colors)];

	$str .= '<tr bgcolor="'.$clr.'"><td class="oddrow" align="center" colspan="2"><strong>'.linkNode($$N{author_user}, '', {lastnode_id=>undef});

	if($cansee) {
		$str .='&nbsp;&nbsp;&nbsp;&nbsp;';
                if($$N{reputation}<0) {
                  $str .= 'R:'.$$N{reputation}.' ';
                }
		if($$N{notnew}){
		  $str .= '(<font color="red">H!</font>)';
                $str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'unhidewriteup', 'hidewriteup'=>$$N{writeup_id}}).'>un-h!</a>)';                }else{
		$str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'hidewriteup', 'hidewriteup'=>$$N{writeup_id}}).'>h?</a>)';
                }
	$str .= '(<font color="red">...</font>)' if $DB->sqlSelect('notnew', 'newwriteup', "node_id=$$N{writeup_id}") != $$N{notnew};        
	}



	$str .='</strong></td>';

	$str .= '</tr><tr><td align="left">';

  if (getNodeById($$N{parent_e2node}) && $$N{parent_title} ne "") {
    $str .= linkNode($$N{parent_e2node}, $$N{parent_title}, {lastnode_id=>undef});
  } else {
    $str .= "(No parent node.)" . $$N{title};
  }

  $str .= '</td><td align="right"><small> ('
          . linkNode($$N{writeup_id}, $$N{type_title},{lastnode_id=>undef})
          .') </small>';
	$str.= "</td></tr>\n";

}
$str.='</font></td></tr></table>';
$str .= parseLinks('<p>([Writeups by Type|Older stuff])</p>');

return $str;
