node.code:
my ($limit) = @_;
$limit ||= 15;

#return "This is a test of the \"fix up expensive code\" system. This is only a test. Back in 20-30 -jaybonci" unless $USER->{title} eq "JayBonci";
if(!exists($HTMLVARS{'group_gods'}{$$USER{node_id}}) && !exists($HTMLVARS{'group_content editors'}{$$USER{node_id}})){  my $blah = getNode('New Writeups Normal', 'nodelet'); return $$blah{nltext};}
#we need this for now


my $UID = $$USER{user_id} || 0;
$limit = $$VARS{num_newwus} if($$VARS{num_newwus});
my $cansee =(exists($HTMLVARS{'group_gods'}{$UID}) || exists($HTMLVARS{'group_content editors'}{$UID}));
my $isgod = ((exists($HTMLVARS{'group_gods'}{$UID}))?1:0);
my $nr = getId(getNode('node row', 'superdoc'));
#$limit = 20 if $cansee;
my $str = '<table width="100%" border="0" cellpadding="0" cellspacing="0">';

my $count=0;
my @colors;
@colors = split(/\s*,\s*/,$$THEME{themeColors}) if exists $$THEME{themeColors};
@colors = ('#CCCC99') unless @colors;

my $nwf = getNode('New Writeups Feeder', 'nodelet');
my @wucache = eval('my '.$$nwf{nltext});
my $found  = 0;
my $iter = 0;
#return $yea[0][1]{title};


#gets a node given the ID
#this caches nodes between hits, so it doesn't hurt to get 1 node a zillion times
#originally in Everything Discussion Directory
#note: this may be completely pointless if E2 caches things anyway, but I don't have much faith in that :-|
#returns undef if unable to get a node
#created: 2001.11.27.n2; updated: 2002.01.23.n3
#author: N-Wing
#my %ids;
#local *getNodeFromID = sub {
#	my $nid = $_[0];
#	return undef unless (defined $nid) && ($nid=~/^\d+$/);
#
#	#already known, return it
#	return $ids{$nid} if exists $ids{$nid};
#
#	#unknown, find that
#	my $N = getNodeById($nid);
#	return undef unless defined $N;
#	return $ids{$nid}=$N;
#};

while($found < $limit) {
#	$iter++; >>> Causes the miss-most-recent display bug.
	my $N = $wucache[0][$iter];
	$iter++;
	last if $N==undef;	#possible since hidden things are skipped

#	$found++ unless($$N{notnew});
#	#$found++;
#	next if(!($cansee) && $$N{notnew}==1);
	if($$N{notnew}==1) {
		#hidden writeups only seen by editors and that writeup's author
		next unless ($cansee or $UID==$$N{author_user});
	} else {
		++$found;
	}

	my $PN = getNodeById($$N{parent_e2node});
	my $WR = getNodeById($$N{wrtype_writeuptype});
#	my $PN = getNodeFromID($$N{parent_e2node});	#sometimes repeated
#	my $WR = getNodeFromID($$N{wrtype_writeuptype});	#repeated many times
	my $clr = $colors[$count++%int(@colors)];
	my $st;
	$st = $$PN{title} if(ref $PN eq 'HASH');
	my $len = 24;
	my @words = split ' ', $st;

	foreach(@words) {
		if(length($_) > $len) {
#			$_ = substr($st, 0, $len);
#			$_ .= '...';
			$_ = substr($_, 0, $len).'...';
		}
	}

	$st = join ' ', @words;

	$str .= '<tr bgcolor="'.$clr.'"><td class="oddrow" align="center" colspan="2"><strong>'.linkNode($$N{author_user}, '', {lastnode_id=>undef});

	if($cansee) {
		$str .='&nbsp;&nbsp;&nbsp;&nbsp;';
		if($$N{notnew}){
		  $str .= '(<font color="red">H!</font>)';
                $str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'unhidewriteup', 'hidewriteup'=>$$N{node_id}}).'>un-h!</a>)';
                }else{
		$str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'hidewriteup', 'hidewriteup'=>$$N{node_id}}).'>h?</a>)';
                }
	$str .= '(<font color="red">...</font>)' if $DB->sqlSelect('notnew', 'newwriteup', "node_id=$$N{node_id}") != $$N{notnew};
        my $kuid = $DB->sqlSelect('linkedby_user', 'weblog', "weblog_id=$nr and to_node=$$N{node_id}") || 0;
        $str .=' (X)' if $kuid;
	}

	$str .= '</strong></td>';
	$str .= '</tr><tr><td align="left">'.linkNode($PN, $st, {lastnode_id=>undef}).'</td><td align="right"><small>('.linkNode($N, $$WR{title},{lastnode_id=>undef}).')</small>';
	$str.= "</td></tr>\n";
}
$str.='</font></td></tr></table>';
