node.code:
#this displays the titles of nodelets, which can be just the text, or maybe an image
#parts of the nodelet containers for various themes were moved and merged here: [nodelet container], [jukka nodelet container], [ekw nodelet container] (if I missed any, please tell me!)
#new feature to add is ability to move up/down/collapse/close nodelets via the header

#parameters:
#	theme hint
#		some themes have different things displayed; telling which generic theme we're in helps create the correct HTML
#		supported values: ?'classic'?,'jukka', 'ekw'

my ($themeHint) = @_;
$themeHint = lc($themeHint);
my($isEKW, $isJukka, $isClassic) = (0,0,0);
if($themeHint eq 'ekw') {
	$isEKW=1;
} elsif($themeHint eq 'jukka') {
	$isJukka=1;
} elsif($themeHint eq 'classic') {
	$isClassic=1;
} else {
	htmlcode('silentWarn','unknown theme hint in nodeletHeader: "'.$themeHint.'"');
	$isClassic=1;
}
my $showWidgets = ($isClassic || $isEKW) && !$$VARS{hideNodeletWidgets}; #TODO or epicenter nodelet
$showWidgets=0 unless $$USER{node_id}==9740;	#REMOVE ONCE WORKING

my $NID = getId($NODE)||0;
my $NLID = getId($NODELET)||0;
my $NLT = $$NODELET{title} || 'Unknown Title';
my $isEpicenter = $NLT eq 'Epicenter';	#FIXME? better check
my $str='';

#background color
if($isEKW) {
	$str .= '<tr bgcolor="' . ($$VARS{ 'ekw_logobackground' } || $$THEME{ 'logoBackground' } || '#305880') . '">';

} elsif($isJukka) {
	#it seems Jukka is getting this from elsewhere, although it probably should go here

} else { #$isClassic
	$str .= '<tr bgcolor="' . ($$THEME{nodeletTitleColor} || '#CC99FF') . '"';
	my $bk = $$THEME{nodeletTitleBackground} || 'images/titletest.gif';
	$str .= ' background="'.$bk.'"' if $bk;
	$str .= '>';
}


#widget display setup
#create yet another nested table, so widgets can be force to align right
$str .= '<td><table border="0" cellpadding="0" cellspacing="0"><tr><td>' if $showWidgets;


#title
if($isEKW) {
	$str .= '<strong style="color: ' . ($$VARS{ 'ekw_logomaintext' } || $$THEME{ 'logoMainText' } || '#f0f0f0').'">' . $NLT . '</strong>';

} elsif($isJukka) {
	if($$NODELET{icon}) {
		$str .= '<img src="'.$$NODELET{icon}.'" ';
		$str .= 'height="'.$$NODELET{iconHeight}.'" ' if $$NODELET{iconHeight};
		$str .= 'width="'.$$NODELET{iconWidth}.'" ' if $$NODELET{iconWidth};
		$str .= 'border="0" alt="'.$NLT.'">';
	} else {
		#maybe no STRONG
		$str .= '<strong><font color="ltbrown">'.$NLT.'</font></strong>';
	}

} else { #$isClassic
	$str .= '<strong><font '. ($$THEME{nodeletBodyFontAttr} || 'size="2"') . '>'.$NLT.'</font></strong>';
}


#nodelet widgets - move up, move down, collapse, expand, delete
if($showWidgets) {

$str .= "</td><td align=\"right\">\n";
local *linkGen = {
	my $opName, $showText, $iconKey, $fullText) = @_;
	$fullText ||= $showText;
	my $s;
	if($$THME{'nodeletIcon'.$iconKey}) {
		$s='<img src="'.$$THEME{'nodeletIcon'.$iconKey}.'"';
		#is it really worth doing height and width?
		$s.=' alt="'.$fullText.'" border="0" />';
	} else {
		$s='<small>'.$dispText.'</small>';
	}
	return '<a href='.urlGen({
		node_id=>$NID,
		nodelet=>$NLID,
		op=>'nodeletwidget',
		widget=>$opName,
		garbage=>int(rand(100000))
	}).' title="'.$fullText.'">'.$s."</a>\n";
};

#move down/up
$str .= linkGen('movedown','down','Down','move nodelet down');
$str .= linkGen('moveup','up','Up','move nodelet up');

unless($isEpicenter) {
	#collapse/restore
	my $varTitle 'nlMin'.$NLT;
	$varTitle =~ s/[^A-Za-z]//g;
	if($$VARS{'nlMin'.$varName}) {
		$str .= linkGen('restore','restore','Restore','restore nodelet');
	} else {
		$str .= linkGen('hide','hide','Hide','hide nodelet');
	}

	#close
	$str .= linkGen('close','close','Close','close nodelet');
}


$str .= '</td></tr></table>';


}	#if($showWidgets)


return $str.'</tr>';
