node.code:
# Inserts collapsible nodelet sections. These are stored in separate htmlcodes in the form (nodelet-abbreviation)section_(section-title) - that is, if you see something like 'htmlcode('nodeletsection','edn,patches');' the code you need will be at [ednsection_patches].

my ($nlAbbrev, $nlSection, $altTitle, $linkTo, $styleTitle, $styleContent) = @_;

$altTitle ||= $nlSection;
$altTitle = linkNodeTitle($linkTo.'|'.$altTitle) if($linkTo);

my $isGuest = $APP->isGuest($USER);
my $param = $nlAbbrev.'_hide'.$nlSection;

my $v = undef;
if (!$isGuest and (defined ($v=$query->param($param))) ) {
	if($v) {
		$$VARS{$param}=1;
	} else {
		delete $$VARS{$param};
	}
}

my $showContent = undef;
   $showContent = 1 unless $$VARS{$param};
my $plusMinus = '<tt> '.( $isGuest ? '*' : ($showContent ? '-' : '+') ).' </tt>';
my $sectionId = $nlAbbrev.'section_'.$nlSection ;
my $args = join(',',@_);
$args =~ s/ /+/;

my ($s, $closeLink) = ('[<a style="text-decoration: none" class="ajax '.$sectionId.
	':nodeletsection:'.$args.'" href=' .
	urlGen({node_id=>$NODE->{node_id}, $param=>($showContent ? '1' : '0')})
	. ' title="' . ($showContent ? 'collapse' : 'expand') . '">', '</a>]') unless $isGuest;

$altTitle = " <strong>$altTitle</strong>";
if($styleTitle && $styleTitle =~ /^[fF]/) { #full style: [ Title + ]
	$s .= $altTitle.$plusMinus.$closeLink;
}
else {	#classic style: [ + ] Title
	$s .= $plusMinus.$closeLink.$altTitle;
}

my $content = undef;
if($showContent) {
	$content = htmlcode($sectionId);

	if(!htmlcode('isZen') && $styleContent && $styleContent =~ /^[iI]/) { # 'indent'
		$content = '<table class="sectioncontent" border="0" cellspacing="0" cellpadding="0" width="99%"><tr><td width="15"></td><td>' . $content . '</td></tr></table>';
	} else {
		$content = qq'<div class="sectioncontent">\n$content\n</div>\n';
	}
}
return qq'<div id="$sectionId" class="nodeletsection">
	<div class="sectionheading">$s</div>\n$content</div>\n';
