node.code:
return unless exists $HTMLVARS{'group_gods'}{$$USER{node_id}};

my $newStr = "<ul>";

my $nt = $$NODE{type}{title};

if ($nt eq 'user') {

	$newStr .="<li>".linkNode($NODE, 'bless', {op=>'bless'}).'</li>';
	$newStr.="<li>".linkNode($NODE, 'bestow 25 votes', {op=>'bestow'})."</li>";
	$newStr.="<li>".linkNodeTitle('bestow cools')."</li>";

} elsif($nt eq 'writeup') {

	$newStr.="<li>".linkNode(1138488,'Reparent Writeup',{old_writeup_id => $$NODE{node_id}})."</li>";
	$newStr.="<li>".linkNode(getNode('Renunciation Chainsaw', 'restricted_superdoc'), "Change author", {'wu_id' => $$NODE{node_id}})."</li>";

}
elsif($nt eq 'e2node') {

	$newStr.="<li>".linkNode(1138488,'Reparent Writeups',{old_e2node_id => $$NODE{node_id}})."</li>";
	$newStr.="<li>".linkNode($NODE, 'Repair Node', {op=>'repair_e2node', repair_id=>$$NODE{node_id}})."</li>";

}

$newStr .="</ul>";

return "<div class='nodelet_section'><h4 class='ns_title'>Node Toolset</h4>".$newStr."</div>";

