node.code:
my ($name) = @_;
$name ||= '';

my $scratchSave = $query->hidden('scratch_id')
  if defined $query->param('scratch_id');

$query->start_form(-method => 'POST',
                   -action => urlGenNoParams($NODE,1),
                   -name => $name,
                   -id => $name) .

$query->hidden('displaytype') .

$scratchSave .

$query->hidden('node_id', getId $NODE);
