node.code:
return unless $APP->isAdmin($USER);
my $str = htmlcode('openform');
$str.= "<input type=\"hidden\" name=\"op\" value=\"orderlock\">";

my $lockornot = undef;

if($NODE->{orderlock_user})
{
  $str.= "Ordering locked by ".linkNode(getNodeById($NODE->{orderlock_user}))." ";

  $str.= "<input type=\"hidden\" name=\"unlock\" value=\"1\">";
  $lockornot = "Unlock Writeup Ordering";

}else
{
  $lockornot = "Lock Writeup Ordering";
}


$str.= "<input type=\"submit\" value=\"$lockornot\">";
$str.="</form>";
return "<p align=\"right\">$str</p>";
