node.code:
my $disabled = shift || htmlcode('getdisabledactions');

my $b ;
my @actions = () ;
push @actions , $b if not $disabled =~ /c/ and $b = htmlcode('coolit','') ;

if ($$NODE{type}{title} eq 'user'){
	my $minLevel = 11;
	my $Sanctificity = 10;
	push @actions , linkNode($NODE, 'sanctify', {op=>'sanctify', -title => "Give 'em 10GP!",
			-id => 'sanctify', -class => 'ajax (sanctify):ajaxEcho:Sanctified!'})
		if !$$VARS{GPoptout} && $$USER{title} ne $$NODE{title} &&
		getLevel($USER) >= $minLevel && $$USER{GP} >= $Sanctificity;
	push @actions , $b if $b = htmlcode('favorite_noder');
}

my $b = htmlcode('bookmarkit' , $NODE , 'Add to bookmarks' ) unless $disabled =~ /b/ ;
my $a = htmlcode( 'categoryform' ) unless $disabled =~ /a/ ;
my $w = htmlcode( 'weblogform' )
	if $$NODE{type}{sqltablelist} =~ /document/ && $$VARS{can_weblog} and not $disabled =~ /w/ ;

my $title = 'Add this '.( $$NODE{ type }{ title } eq 'e2node' ? 'entire page' :
		$$NODE{ type }{ title } ).' to a ' ;

unless ( $query -> param( 'addto' ) ) {
	push @actions , $b if $b ;
	push @actions , htmlcode( 'widget' , $a , 'form' , 'Add to category&hellip;' ,
		{ showwidget => 'category' , -title => $title.'category' } ) if $a ;
	push @actions , htmlcode( 'widget' , $w , 'form' , 'Add to page&hellip;' ,
		{ showwidget => 'weblog' , -title => $title.' usergroup page' } ) if  $w ;
} else {
	push @actions , htmlcode( 'widget' ,
		$query -> hidden( 'addto' )."<small>$b</small><hr>\n$a\n$w" , 'form' ,
		'Add to&hellip;' ,
		{ showwidget => 'addto'.$$NODE{ node_id } , -title => $title.'category or usergroup page' } ) ;
}

my $disable = linkNode( getNode( 'Disable actions' , 'superdoc' ) , '<small>x</small>' ,
	{ '-title' => 'Disable some/all actions for this node/nodetype&hellip;' ,
	donode => $$NODE{node_id} } ) . "</li>\n<li>"
		if $APP->isEditor($USER) and not $disabled =~ /L/ ;

return '<ul class="topic actions">
	<li>' . $disable . join( "</li>\n<li>" , @actions ) . "</li>\n</ul>\n" if @actions ;
return '' ;
