node.code:
#Pass the starting page, number of things to show per page, how many
#are there in this page.
my ($offset,$limit,$numthispage) = @_;

my $str;

if ($offset > 0 or $numthispage == $limit) {
  $str .= '<p align="right">';
  if ($offset > 0) {
    my ($start,$end); $end = $offset; $start=$offset-$limit+1;
    $str .= linkNode($NODE,"prev $start &ndash; $end",
                     {offset => $offset-$limit});
    $str .= "<br />";
  }

  my $bot = $offset+1;
  my $top = $offset+$numthispage;
  $str .= "Now: $bot &ndash; $top <br/>";

  if ($numthispage == $limit) {
    my ($start,$end); $start = $offset+$limit+1; $end=$offset+2*$limit;
    $str .= linkNode($NODE,"next $start &ndash; $end",
                     { offset => $offset+$limit});
    $str .= "<br />";
  }
  $str .= "</p>\n";
}

return $str;
