node.code:
my ($k, @title) = @_;

# Similar to varcheckbox

return if ($APP->isGuest($USER)) || ($$USER{title} eq 'everyone');
my $title = join ', ', @title;
$title ||= $k;

my $value = 0;
if($query->param('setparam_'.$k)) {
	$APP->setParameter($USER, $USER, $k, 1);
        $value = 1;
} elsif($query->param('sexisgood')) {
	$APP->delParameter($USER, $USER, $k);
}

$query->checkbox('setparam_'.$k, $value, '1', $title);
