node.code:
my ($ref,$subref,%display,$text,$temp_ref);
my $node_id = $NODE->{node_id};

$text .=<<EOD;
<script language='javascript'>

function addcat()
{
	var newname = prompt("Enter a Category Name:", "");

 	if(newname)
  	{
    		var url = "index.pl?type=category&node=NEWNAME&op=new&lastnode_id=$node_id";

    		url = url.replace(/NEWNAME/, newname);
    		url = url.replace(/ /g, "%20");
    		parent.location = url;
  	}
}
</script>
<script>
USETEXTLINKS = 1
STARTALLOPEN = 0
USEFRAMES = 0
USEICONS = 0
WRAPTEXT = 1
PERSERVESTATE = 1
EOD


my $CAT = getNode('category', 'nodetype');

my $garbage;
my @parent;
my $user = $USER->{author_user};
my $title = $USER->{title};


if($USER->{realname} =~ /archive/i)
{
	@parent = getNodeWhere("type_nodetype = $$CAT{node_id} and author_user = $user and title = 'class_root'","", "createtime DESC");
}
else
{
	@parent = getNodeWhere("type_nodetype = $$CAT{node_id} and author_user = $user and title = '$title\_root'","", "createtime DESC");
}


foreach my $node (@parent) 
{
	$$node{title} =~ s/'/\\'/g;
	$text .= "foldersTree = gFld('$$node{title}','index.pl?node_id=$$node{node_id}&tree=personal&lastnode_id=$node_id')\n";
	$text .= "f = foldersTree\n";

	my $children = $DB->sqlSelectMany ("*","nodegroup","nodegroup_id=$$node{node_id}","order by node_id");
	my $child_ref;
	while ($child_ref = $children->fetchrow_hashref )
	{
		my $DOC = getNodeById($$child_ref{node_id});
		if($$DOC{type_nodetype} != $$CAT{node_id})
		{
			$$DOC{title} =~ s/'/\\'/g;
			$text .= "insDoc(f,gLnk('R', '$$DOC{title}', 'index.pl?node_id=$$DOC{node_id}&tree=personal&lastnode_id=$node_id'))\n";
		}
		else 
		{
			$$DOC{title} =~ s/'/\\'/g;
			$text .= "fSub = insFld(f,gFld('$$DOC{title}', 'index.pl?node_id=$$DOC{node_id}&tree=personal&lastnode_id=$node_id'))\n";
			my $sub = $DB->sqlSelectMany ("*","nodegroup","nodegroup_id=$$DOC{node_id}","order by node_id");
			while ($subref = $sub->fetchrow_hashref )
			{
				my $temp = $DB->sqlSelectMany ("*","node","node_id=$$subref{node_id}");
				while ($temp_ref= $temp->fetchrow_hashref )
				{
					if($$temp_ref{type_nodetype} eq $$CAT{node_id})
					{
			 			if($display{$$temp_ref{node_id}} eq "")
						{
							$$temp_ref{title} =~ s/'/\\'/g;

							$text .= "fSubSub = insFld(fSub,gFld('$$temp_ref{title}', 'index.pl?node_id=$$temp_ref{node_id}&tree=personal&lastnode_id=$node_id'))\n";
							$display{$$temp_ref{node_id}} = 1;
						}

						my $test = $DB->sqlSelectMany("*","nodegroup","nodegroup_id=$$temp_ref{node_id}","order by node_id");
						my $test_ref;
						while ($test_ref= $test->fetchrow_hashref )
						{
							my $one = $DB->sqlSelectMany("*","node","node_id=$$test_ref{node_id}");
							my $one_ref;
							while ($one_ref= $one->fetchrow_hashref )
							{
								#$garbage .= "one_ref{node_id} = $$one_ref{node_id}<br>";
								if($$one_ref{type_nodetype} eq $$CAT{node_id})
								{
									if($display{$$one_ref{node_id}} eq "")
									{
										$$one_ref{title} =~ s/'/\\'/g;
										$text .= "fSubSubSub = insFld(fSubSub,gFld('$$one_ref{title}', 'index.pl?node_id=$$one_ref{node_id}&tree=personal&lastnode_id=$node_id'))\n";
										$display{$$one_ref{node_id}} = 1;
									}

									my $two = $DB->sqlSelectMany("*","nodegroup","nodegroup_id=$$one_ref{node_id}","order by node_id");
									my $two_ref;
									my @nodegroups;
									while ($two_ref= $two->fetchrow_hashref )
									{
										push @nodegroups,$$two_ref{node_id};
									}

									#$garbage .= "scalar 1 = " . scalar(@nodegroups) ."<br>";
									foreach my $node (@nodegroups)
									{
										#$garbage .= "<br>node = $node<br>";
										my $three = $DB->sqlSelectMany("*","node","node_id=$node","order by node_id");
										my $three_ref;

										while ($three_ref= $three->fetchrow_hashref )
										{

											if(($display{$$three_ref{node_id}} eq ""))
											{
												$display{$$three_ref{node_id}} = 1;
												$$three_ref{title} =~ s/'/\\'/g;
												if($$three_ref{type_nodetype} != $$CAT{node_id})
												{
													$text .= "insDoc(fSubSubSub,gLnk('R', '$$three_ref{title}', 'index.pl?node_id=$$three_ref{node_id}&tree=personal&lastnode_id=$node_id'))\n";
												}
												else
												{
													#$garbage .= "$$three_ref{node_id}, $$three_ref{title}<br>";
													my $four = $DB->sqlSelectMany("*","nodegroup","nodegroup_id=$$three_ref{node_id}","order by node_id");
													my $four_ref;
													while ($four_ref = $four->fetchrow_hashref)
													{
														push @nodegroups,$$four_ref{node_id};
														#$garbage .="pushing $$four_ref{node_id}<br><br>";
													}

													next;
												}
											}
										}
									}
								}
								else
								{
									if($display{$$one_ref{node_id}} eq "")
									{
										$$one_ref{title} =~ s/'/\\'/g;
										$text .= "insDoc(fSubSub,gLnk('R', '$$one_ref{title}', 'index.pl?node_id=$$one_ref{node_id}&tree=personal&lastnode_id=$node_id'))\n";
										$display{$$one_ref{node_id}} = 1;
									}
								}
							}
						}
                 			}
           				elsif ($display{$$temp_ref{node_id}} eq "")
					{
						$$temp_ref{title} =~ s/'/\\'/g;
						$text .= "insDoc(fSub,gLnk('R', '$$temp_ref{title}', 'index.pl?node_id=$$temp_ref{node_id}&tree=personal&lastnode_id=$node_id'))\n";
						$display{$$temp_ref{node_id}} = 1;
					}
				}
			}
		}
	}
}


if(scalar(@parent) == 0)
{
	$text .= "foldersTree = gFld('/', 'javascript:parent.op();')\n";
	$text .= "f = foldersTree\n";
}


$text .=<<EOD;
	</script>
	<p>$garbage</p>
EOD

return $text;
