node.code:
#ReadThis - C! writeups

  my $str = '<ul>';
  my $csr = $DB->sqlSelectMany("*", "coolwriteups", "", "order by tstamp desc limit 15");

  while(my $row = $csr->fetchrow_hashref()){
    my $cw = getNodeById($$row{coolwriteups_id});
    next unless($cw);
    my $np = getNodeById($$cw{parent_e2node});
    $str .= '<li>'.linkNode($cw, $$np{title}, {lastnode_id => 0});
    $str .= ' <small>('.linkNode($np,'full',{lastnode_id =>0}).")</small></li>\n";
  }

  $csr->finish();
$str.='</ul>';
  return $str;
