node.code:
my ($elements, $textarea) = @_;
my $mode = 'none';
my ($hideCSS, $hideJS, $preamble) = ('', '', '');
my ($textareaId, $toolbardivName) = (undef,undef);

##########
# name of the textarea form element is different for different areas
# so we need to dynamically change what the HTMLtoolbar is pointing at

  # if homenode editing
if ($$NODE{type}{title} eq "user") {

  $textareaId = 'user_doctext';

} # otherwise we're using writeup_doctext
else  {

  $textareaId = 'writeup_doctext';

}

$toolbardivName = "ed_toolbar_" . $textareaId;
my $toolBarLink =
  urlGen({"node" => "HTMLtoolbar", 
    "type" =>"rawdata", "displaytype" =>"raw" });
my $paramMCE = $query->param("tinyMCE");

if ($paramMCE == 1)       { $$VARS{settings_useTinyMCE} = 1; }
elsif ($paramMCE == -1)   { $$VARS{settings_useTinyMCE} = 0; }

# Hide the HTML toolbar if Tiny MCE is in use
if ($$VARS{settings_useTinyMCE}) {

  $mode = 'exact';
  $hideJS = "\njQuery('#$toolbardivName').hide();";
  $hideCSS = <<ENDCSS;

  <style type="text/css">
    #${toolbardivName} {
      visiblity: hidden;
    }
  </style>
ENDCSS

}

$preamble = <<PREAMBLE;

<script type="text/javascript" src="/js/tiny_mce/tiny_mce.js"></script>
<p><a href="javascript:toggleEditor('$textareaId');">
Click to toggle <abbr title='What you see is what you get'>WYSIWYG</abbr>
editing.</a></p>

<script type="text/javascript" src=$toolBarLink></script>
<script type="text/javascript">
  var edCanvas = document.getElementById("$textareaId");
  // Calling edToolbar() uses write() to insert the toolbar
  //  at the present location in the DOM
  edToolbar('$textareaId');
  $hideJS
</script>

PREAMBLE

return ($hideCSS . $preamble . $textarea . '
<script type="text/javascript">
if (window.tinyMCE) {
  tinyMCE.init({
    theme: "advanced",
    theme_advanced_disable : "styleselect,image,anchor,link,unlink",
    mode : "'.$mode.'",
    inline_styles : false,
    button_tile_map : true,
    elements: "'.$elements.'",
    entity_encoding: "raw",
    invalid_elements: "a,img,table,tr,td,th,div,span",
    remove_linebreaks: false,
    extended_valid_elements: "big,small,i,b,tt"
  });
}

function toggleEditor(id) {
  if (typeof(tinyMCE)!="undefined"){
    var toolbarId = "ed_toolbar_" + id;

    if (!tinyMCE.getInstanceById(id)) {
      tinyMCE.execCommand(\'mceAddControl\', false, id);
      jQuery("#"+toolbarId).hide();
    }

    else {
      tinyMCE.execCommand(\'mceRemoveControl\', false, id);
      jQuery("#"+toolbarId).show();
    }
  }
}


</script>');
