node.code:
#originally in [chatterbox] (nodelet)

return 'If you '.linkNode(getNode('Create A New User','superdoc'),'get a (<strong>free</strong>) account',{lastnode_id=>0}).', you can talk here.' if $APP->isGuest($USER);
return '<p><em>your earplugs are in</em></p>' if $$VARS{publicchatteroff};
#we need to delete old stuff too
my $str;
my $wherestr = 'for_user=0 and unix_timestamp(now())-unix_timestamp(tstamp) < 300';
$wherestr.=' and room='.$$USER{in_room} unless ($$VARS{omniphonic});

my $csr = $DB->sqlSelectMany('*', 'message', $wherestr, "order by tstamp");

my $lines;
my @msgs;
while(my $MSG = $csr->fetchrow_hashref) {
	$lines++;
	push @msgs, $MSG;
}
if($lines >= 15) {
	@msgs = splice(@msgs, -15);
}

my $aid;	#message's author ID
foreach my $MSG (@msgs) {
	my $text = $$MSG{msgtext};
	$text =~ s/(\S{22})/$1 /g;
#	$text = encodeHTML($text);
	$text =~ s/\</\&lt\;/g;
	$text =~ s/\>/\&gt\;/g;
#	$text =~ s/\"/\&quot\;/g;

        if (not int(rand(5))%5) {
        my $array = [ split /\s/, $text ];

        my $i;
        for ($i = @$array; --$i; ) {
        my $j = int rand ($i+1);
        next if $i == $j;
        @$array[$i,$j] = @$array[$j,$i];
    }

        $text = join " ", @$array;
        }
	$aid = $$MSG{author_user} || 0;

	if($$MSG{room} != $$USER{in_room}) {
		if($$MSG{room}) {
			my $R = getNodeById($$MSG{room});
			$str.='('.linkNode($R, $$R{abreviation}).')';
		} else {
			$str.='(out)';
		}
	}
	$text = parseLinks($text);

	#N-Wing/wharfinger spending way too much time on a stupid thing ...
	#change *stuff* into HTML bold, /stuff/ into italics
	if($text =~ /^\/me .*/) {
		$text =~ s/(\/\w+\/)/<\/i>$1<i>/g;
		$text =~ s/(\*\w+\*)/<\/i><strong>$1<\/strong><i>/g;
	}

	#  Oh ye of HTML entities, even unto malformed ones: Your 
	# tricks are no match for my '1337 regex skillz.
	$text =~ s/(\.| |\&nbsp;?)+$/./;

	#do \n stuff, but since this is easy to abuse, only let certain people do it
	$text =~ s/\s+\\n\s+/<br \/>/g if exists $HTMLVARS{group_gods}{$aid};

	my $userlink = linkNode( $$MSG{author_user} );
	# Make bones and nate look impressive.
	$userlink =~ s/^(.*)$/<strong>$1<\/strong>/ if ( $aid == 282 || $aid == 220 );

	if ( $text =~ /^\/me (.*)/ ) {
		$str .= '<i>' . $userlink . ' ' . $1 . '</i>';
	} else {
		$str .= '&lt;' . $userlink . '&gt; ' . $text;
	}
	$str.="<br />\n";
}


if(not $lines) {
	#my $EDB = int(rand(100));
        my $EDB = 100;
	my $response = '<i>and all is quiet...</i>';

	if($EDB<25) {
		my @borgspeak = (
		'grrrrr...', '/me hungry!', '/me smells blood',
		"$$USER{title} looks tasty.",
		'<i>you feel its eyes watching you</i>',
		'/me is watching you',
		'/me coughs politely and eats your soul',
		'/me tries to bite your toe',
		'/me starts eating your hair',
		'/me whispers the names of forgotten demons in your ear',
		"mmmm $$USER{title} food"
		);

		$response = $borgspeak[int(rand(@borgspeak))];

		my $edblink = linkNodeTitle('EDB');
		if($response =~ /\/me/) {
			$response =~ s/\/me /<i>$edblink /;
			$response .= '</i>';
		} else {
			$response = "&lt;$edblink&gt;" . $response;
		}
	}

	return $response;
}

$str;
