#displays footer information in its own table
#originally in [jukka container]

my $str = "";

$str = '<table cellpadding="3" cellspacing="0" border="0" width="100%">'."\n".
	'<tr><td><img src="http://static.everything2.com/clear.gif" width="1" height="25" alt="" /></td></tr>'."\n<tr";
$str .= ' bgcolor="#cccc99"'; #FIXME theme-generated
$str .= '><td align="center" style="border:solid black 1px;">';

my @R = (
	'This affordable entertainment brought to you by',
	'E2 is a by-product of the existence of',
	'Everything 2 is brought to you by the letter C and',
	'This page courtesy of',
);

$str.=$R[int(rand(@R))];
$str.=' <a href="http://everything2.com/">Everything2 Media LLC</a>';

$str.="<br />";

$str .= "</td></tr>\n<tr><td";
$str .= ' bgcolor="#003366"'; #FIXME theme-driven
$str .= '><img src="http://static.everything2.com/clear.gif" width="1" height="1" border="0" alt=" " /></td></tr>';
$str .= "\n</table>\n";

$str;
