node.code:
#shows the left-side or right-side nodelets
#parameter: 'left' or 'right' (default)

my ($curSide) = @_;
my $onRight = 0;
my $useTable = $$THEME{noTable} ? 0 : 1;

if($curSide =~ /^l/) {
	return '';	#NPB FIXME this is until the nodelet meta-container knows right and left
	$curSide = 'left';
	$onRight = 0;
} else {
	$curSide = 'right';
	$onRight = 1;
}

my $str = '';

$str .= htmlcode('nodelet meta-container', $curSide);

if(length($str) && $useTable) {
	#NPB TODO nodelet width shouldn't be hard coded here
	$str = "<td width=\"170\" valign=\"top\">\n" . $str . "\n</td>\n";
}

#add a little gap between main contents and nodelets
my $spaceCol = '<td width="2">&nbsp;</td>';
if ($useTable) {
	if($onRight) {
		$str = $spaceCol . $str;
	} else {
		$str .= $spaceCol;
	}
}

my $isZen = htmlcode('isZen','');
## zen theme 1854354 = Kyle's Style, the only theme this was designed for
if ( ($$VARS{nodelet_tabs}) && ($isZen) && ($$VARS{userstyle}==1854354) ) {
	my $specialStr = "";

	my $curr = $$VARS{current_nodelet};
	my $currNodelet = undef;

	my @nodelets = split(',',$$VARS{nodelets});
	if ($APP->isEditor($USER) ) {
		my $nl = getNode('Master Control','nodelet');
		unshift(@nodelets, getId($nl));
	}

	foreach (@nodelets) {
		$currNodelet = insertNodelet($_) if ($_ == $curr);

		my $N = getNodeById($_);
		getRef $N;

		$specialStr .= "
		<span class='nodelet_tab ".( $_ == $curr ? "nodelet_selected" : "")."' id='nt_".$$N{node_id}."'>$$N{title}</span>
		<div class='hidden' id='nc_".$$N{node_id}."'>".insertNodelet($_)."</div> ";
	}

	$specialStr.="<div id='nodelet_content'>$currNodelet</div>";

	$str = $specialStr;
}

return $str;
