node.code:
return unless $DB->isApproved($NODE, getNode('users with image', 'nodegroup')) or getLevel($NODE) >= 1;
return if isSuspended($NODE,"homenodepic");
return unless $$NODE{imgsrc};
my $imgsrc = $$NODE{imgsrc};
if($Everything::CONF->{homenode_image_host} =~ /s3/)
{
 $imgsrc = "$$NODE{title}";
 $imgsrc =~ s/\W/_/g;
}
$imgsrc = "/$imgsrc" if ($imgsrc !~ /^\//);
'<img src="http://'.$Everything::CONF->{homenode_image_host}.$imgsrc.'" id="userimage">';
