node.code:
use JSON;

$$VARS{fxDuration} = '1' if (delete $$VARS{notransitions});

my $lastnode = $$NODE{node_id};
$lastnode = $$NODE{parent_e2node} if $$NODE{type}{title} eq 'writeup';
$lastnode = $query->param("lastnode_id")||0
	if $$NODE{title} eq 'Findings:' && $$NODE{type}{title} eq 'superdoc';

my $e2; 
$e2->{node_id} = $$NODE{node_id};
$e2->{lastnode_id} = $lastnode;
$e2->{title} = $$NODE{title};
#$e2->{ignoreMissingLibraries} = 1;

my $cookie;
foreach ('fxDuration', 'collapsedNodelets', 'settings_useTinyMCE', 'autoChat', 'inactiveWindowMarker'){
	if (!$APP->isGuest($USER)){
		$$VARS{$_} = $cookie if $cookie = $query -> cookie($_);
		delete $$VARS{$_} if $cookie eq '0';
	}
        $e2->{$_} = $$VARS{$_} if ($$VARS{$_});
}

$e2 -> {collapsedNodelets} =~ s/\bsignin\b// if $query -> param('op') eq 'login';
$e2 = encode_json($e2);

my $min = '.min' unless $APP->inDevEnvironment();
my $libraries = qq'<script src="http://code.jquery.com/jquery-1.4.4$min.js" type="text/javascript"></script>';
# mark as guest but only in non-canonical domain so testing and caching both work


my $GUjs = "<script src='http://jscss.everything2.com/GuestJavascript.js' type='text/javascript'></script>";
my $js_decisions = htmlcode('javascript_decider');

unless ($APP->isGuest($USER)){
	$libraries .= '
	<script src="http://jscss.everything2.com/jquery-ui.min.js" type="text/javascript"></script>';
	$GUjs = "";
}

$libraries .= <<LIBS;
	<script src="http://jscss.everything2.com/jquery.ba-bbq$min.js" type="text/javascript"></script>
LIBS

$libraries .= <<DEVLIBS . htmlcode('nagger') if $APP->inDevEnvironment();
	<script src="http://jscss.everything2.com/stars/jquery.MetaData.js" type="text/javascript"></script>
	<script src="/node/jscript/star+script" type="text/javascript"></script>
DEVLIBS

return <<ENDJS;
	<script type='text/javascript' name='nodeinfojson' id='nodeinfojson'>
		e2 = $e2;
	</script>
        $libraries
        $js_decisions
        $GUjs
ENDJS
