node.code:
#   This generates a link which launches a little window with a "color swatch" in it; 
#   see [color_swatch] for that. You'll need to include [popwindow] somewhere, for 
#   the pop_small() JavaScript function.

my( $formname, $fieldname ) = @_;

my $str = '';

$str .= ' (<a alt="Show color swatch" ';
$str .= 'href="javascript:pop_small( \'index.pl?node=color_swatch&bgcolor=\' + escape( ';
#   Aarrgghh! Look at all them dots! 
$str .= 'document.forms.' . $formname . '.' . $fieldname . '.value ), \'color_swatch\' );">swatch</a>)';

$str;
