node.code:
my $N;


if ($$NODE{type}{title} eq 'writeup') {
  $N = getNodeById $$NODE{parent_e2node};  
}
$N ||= $NODE;

my %bob;
foreach (keys %HTMLVARS) { $bob{$HTMLVARS{$_}} = 1; }

return if $bob{getId($N)};
my $cantrim;
$cantrim = 1 if exists $HTMLVARS{group_gods}{getId($USER)};

#return "note";
#return unless $$USER{title} eq 'nate';

#my $csr = $dbh->prepare("select food, links.hits, #node_id, title, type_nodetype from links, node where " 
#   ."from_node=".getId($N)." and to_node=node_id " 
#   ." and (type_nodetype=".getId(getType('e2node'))
#  . " or type_nodetype=".getId(getType('superdoc')).")"
#  ." order by links.hits limit 100");
my $csr = $dbh->prepare("select * from links where
 from_node=".getId($N)." order by links.hits desc limit 60");


$csr->execute;
my @nodelinks;
while (my $link = $csr->fetchrow_hashref) {
  push @nodelinks, $link;  
}
$csr->finish;
my $linx = \@nodelinks;#selectLinks($N, 'hits');
return '' unless(@$linx);
my $str = '';

$str.='<TABLE cellpadding=10 cellspacing=0 border=0 width=100%><TR>';
my $n=0;

my $maxval;
if ($$THEME{bodyBgColor} =~ /^\#(\w\w)(\w\w)(\w\w)$/)
 { $maxval = [hex($1),hex($2),hex($3)]; }
$maxval ||= [255,255,255];

my $e2nodetype = getId(getType('e2node'));
   my $range = { 'min' => [170,170,170], 
      'max' => $maxval, 
      'steps' => int(@$linx) };
my $step = 0;
foreach my $l (@$linx)
{        
   my $node = getNodeById($$l{to_node}, 'light');
   next if $$node{type_nodetype} != $e2nodetype;
   if(($node) && !($bob{getId($node)}))
   {
      my $curr = "#";

      foreach (0..2) { 
       $curr .= sprintf("%02x", $$range{max}->[$_] -
   (($$range{max}->[$_] -     $$range{min}->[$_])/$$range{steps}) *$step);
      }

      #$curr = sprintf("%02x", $curr);
      #$curr = "#".$curr x3;
      $step++;

      next if ($$N{node_id} == $$node{node_id});
      $str.= '</TR><TR>' if(($n) && !($n%4));
      my $attr = "";
      $attr .= " bgcolor=$curr" unless $$VARS{nogradlinks};
      $str.= "<TD $attr>$curr";
      $str.= $query->checkbox("cutlinkto_".$$node{node_id}, 0, "1", "") if $cantrim;
      $str.= linkNode($node, $$node{title}, {lastnode_id=>getId($N)}) ;
      $str.= " ($$l{hits})" if exists $HTMLVARS{group_gods}{getId($USER)};;
      $str.="</TD>";
      $n++;
   }
}
for(;$n%4;$n++) { $str.='<TD width=25%></TD>'; }
$str.="</TR></TABLE>\n";

if ($cantrim) {
  $str = htmlcode('openform')
    ."<INPUT type=HIDDEN name=op value=linktrim>"
    ."<INPUT type=HIDDEN name=cutlinkfrom value=$$N{node_id}>"
    . $str
    . $query->submit("go","trim links") . "</form>";
}

return $str if exists $$THEME{noThinOutline};
containHtml('thin outline container', $str);
