node.code:
# [{uploadimage:field}]
#
# generates an input file field, and sets the field to the URL 

use File::Copy;

my ($field) = @_;
my $str = "";
my $name = $field."_file";
my $tmpfile = '/tmp/everythingimage' . rand(10000); 
my $imagedir = '/var/everything/www/images/incoming';
my $imageurl = 'images/incoming/';

my $fname = $query->param($name);

if (defined $fname) {

  my $imgname = $fname;
  $imgname =~ s/[^\w\.]*//g;

  $str.="<p>Received " . encodeHTML($fname) . " of type ";
  $str.= "'". $query->uploadInfo($fname)->{"Content-Type"}."'!</p>";

  open OUTFILE, ">$tmpfile";
  my ($buf);
  while (read($fname, $buf, 1024)) { 
    print OUTFILE $buf;
  }
  close OUTFILE;

  move($tmpfile, "$imagedir/$imgname");
  $$NODE{$field} = $imageurl . $imgname;
  updateNode($NODE, $USER);

}

$str .= "NOTE: Image directory is hard-coded in "
     . linkNode(getNode('uploadimage', 'htmlcode'))
     . "<br>"
     ;
$str .= $query->filefield($name);

$str;
