node.code:
my ($uname) = @_;

my $U = getNode($uname, 'user');
$U ||= getNode($uname, 'usergroup');
if ($U) {
	return if $$U{title} eq 'EDB';
	unless ($DB->sqlSelect('*',
                         'messageignore',
                         "messageignore_id=$$USER{node_id}
                          AND ignore_node=$$U{node_id}")) {
		return 'not yet ignoring '.$$U{title};
	}
	else {
		$DB->sqlDelete('messageignore',
                   "messageignore_id=$$USER{node_id}
                    AND ignore_node=$$U{node_id}");
	}
} 
else {
       $uname = encodeHTML($uname);
	return "<strong>$uname</strong> doesn't seem"
         ." to exist on the system!" unless $U;
}

$query->param('DEBUGignoreUser', 'tried to unignore '.$$U{title});
return "$$U{title} unignored";
