sa.name = 'sa';
sa.value = 'Search';

searchForm.appendChild(cx);
searchForm.appendChild(cof);
searchForm.appendChild(sa);

\$('node_search').name = 'q';

}
return true;
}
</script>

";

$str .= $query->start_form("GET",$query->script_name, "onSubmit='return fullText();'");
$str .= '<table cellpadding="0" cellspacing="0"><tr valign="middle">';


$str.= '<td>'.
 $query->textfield(-name => 'node',
-id => 'node_search',
  -default => $default,
  -size => 28,
  -maxlength => 80);
$str.='<input type="hidden" name="lastnode_id" value="'.$lnid.'" />';

$str.='</td><td>';
if (exists $$THEME{searchIcon}) {
  my $iconsrc = $$THEME{searchIcon};
  if($$THEME{searchIcon} =~ m|^http://|)
  { 
    $iconsrc = $$THEME{searchIcon};
  }
  $str.= '<input type="image" name="searchy" alt="search" align="middle" src="'.$iconsrc.'"';
  $str.= ' width="'.$$THEME{searchIconWidth}.'"' if exists $$THEME{searchIconWidth};
  $str.= ' height="'.$$THEME{searchIconHeight}.'"' if exists $$THEME{searchIconHeight};
  $str.=' />';
} else {
 $str.='<input type="submit" name="searchy" value="search" />';
}
 $query->param('node', $title); 

#$str.= '</td><td style="font-family:sans-serif;"><small><small>';
