node.code:
return "<!-- noad:vars -->" unless $$VARS{zenadinheader};
my $SETTINGS = getVars(getNode('system settings','setting'));
my $ad_text = '';
if ($$SETTINGS{ad_location} eq 'header' && $$USER{user_id} == $HTMLVARS{guest_user}) {
  $ad_text = htmlcode( 'googleads' );
  $ad_text = '<div class="headerads">'.$ad_text.'</div>' if $ad_text;
}else{
  return "<!-- noad:settings -->";
}
return $ad_text;
