node.code:
my $lastnodeId = $query->param("softlinkedFrom");
$lastnodeId ||= $query -> param('lastnode_id') unless $$USER{title} eq 'Guest User';

my $lastnode = getNodeById($lastnodeId)
  if defined $lastnodeId;
my $default = $$lastnode{title} if $lastnode;

my $str = $query->start_form(
  -method => "GET"
  , -action => $query->script_name
  , -id => 'search_form'
  ).
  $query->textfield(-name => 'node',
  	value => $default,
  	force => 1,
   -id => 'node_search',
   -size => 28,
   -maxlength => 230)
  ;

my $lnid = $$NODE{parent_e2node} if
	$$NODE{type}{title} eq 'writeup'
	and $$NODE{parent_e2node}
	and getNodeById($$NODE{parent_e2node});
$lnid ||= getId($NODE);

$str.='<input type="hidden" name="lastnode_id" value="'.$lnid.'">';
$str.='<input type="submit" name="searchy" value="search" id="search_submit" title="Search within Everything2">';

$str.=qq'\n<span title="Include near matches in the search results">'.$query->checkbox(
 -id => "near_match",
 -name => 'soundex',
 -value => '1',
 checked => 0,
 force => 1,
 -label => 'Near Matches'
) . "</span>";

$str.=qq'\n<span title="Show all results, even when there is a page matching the search exactly">'.$query->checkbox(
 -id => "match_all",
 -name => 'match_all',
 -value => '1',
 checked => 0,
 force => 1,
 -label => 'Ignore Exact',
) . "</span>";

return $str . "\n</form>";
